CREATE TABLE [tb_cliente]( [id_cliente] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [nome] VARCHAR(200) NOT NULL, [razao_social] VARCHAR(200), [cnpj] INTEGEREGER64(14), [cpf] INTEGEREGER64(11), [endereco] VARCHAR(200), [coord_x] INTEGEREGER64, [coord_y] INTEGEREGER64, [ativo] BOOLEAN NOT NULL DEFAULT 1);
CREATE TABLE [tb_produto]( [id_produto] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [nome] VARCHAR(50) NOT NULL ON CONFLICT FAIL, [valor_unitario] DECIMAL NOT NULL ON CONFLICT FAIL, [foto] VARCHAR(300) NOT NULL, [valor_final] DECIMAL NOT NULL, [ativo] BOOLEAN NOT NULL, [qtd_limite] INTEGEREGEREGER NOT NULL DEFAULT 0);
CREATE TABLE [tb_forma_pagto]( [id_forma_pagto] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [cod_forma_pagto] INTEGER NOT NULL, [descr_forma_pagto] VARCHAR(200) NOT NULL, [val_perc] DECIMAL NOT NULL);
CREATE TABLE [tb_status_venda]( [id_status_venda] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [cod_status] INTEGER NOT NULL, [descr_status] VARCHAR(30) NOT NULL);
CREATE TABLE [tb_vendedor]( [id_vendedor] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [nome] VARCHAR(50) NOT NULL);
CREATE TABLE [tb_tipo_telefone]( [id_tipo_telefone] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [descr_tipo_telefone] VARCHAR(30) NOT NULL);
CREATE TABLE [tb_tipo_pedido]( [id_tipo_pedido] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [cod_tipo_pedido] INTEGER NOT NULL, [descr_tipo_pedido] VARCHAR(30) NOT NULL);
CREATE TABLE [tb_estoque]( [id_estoque] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [id_produto] INTEGER NOT NULL ON CONFLICT FAIL REFERENCES tb_produto([id_produto]) ON DELETE CASCADE, [quantidade] INTEGER NOT NULL DEFAULT 0);
CREATE TABLE [tb_pedido]( [id_pedido] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [valor_total] DECIMAL NOT NULL, [endereco] VARCHAR(200) NOT NULL ON CONFLICT FAIL, [coord_x] INT64, [coord_y] INT64, [id_forma_pagto] INTEGER NOT NULL REFERENCES tb_forma_pagto([id_forma_pagto]) ON DELETE NO ACTION); 
CREATE TABLE [tb_pedido_produto]( [id_pedido_produto] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [id_pedido] INTEGER NOT NULL ON CONFLICT FAIL REFERENCES tb_pedido([id_pedido]) ON DELETE CASCADE ON UPDATE CASCADE, [id_produto] INTEGER NOT NULL ON CONFLICT FAIL REFERENCES tb_produto([id_produto]) ON DELETE RESTRICT, [quantidade] INTEGER NOT NULL ON CONFLICT FAIL, [id_tipo_pedido] INTEGER NOT NULL REFERENCES tb_tipo_pedido([id_tipo_pedido]) ON DELETE CASCADE);
CREATE TABLE [tb_telefone]( [id_telefone] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [id_cliente] INTEGER NOT NULL REFERENCES tb_cliente([id_cliente]) ON DELETE CASCADE, [telefone] INTEGER NOT NULL, [id_tipo_telefone] INTEGER NOT NULL REFERENCES tb_tipo_telefone([id_tipo_telefone]) ON DELETE NO ACTION);
CREATE TABLE [tb_venda]( [id_venda] INTEGER PRIMARY KEY ASC ON CONFLICT FAIL AUTOINCREMENT NOT NULL, [id_cliente] INTEGER NOT NULL ON CONFLICT FAIL REFERENCES tb_cliente([id_cliente]) ON DELETE SET DEFAULT ON UPDATE SET DEFAULT, [id_vendedor] INTEGER NOT NULL ON CONFLICT FAIL REFERENCES tb_vendedor([id_vendedor]) ON DELETE CASCADE ON UPDATE CASCADE, [id_pedido] INTEGER NOT NULL ON CONFLICT FAIL REFERENCES tb_pedido([id_pedido]) ON DELETE RESTRICT ON UPDATE NO ACTION, [id_status_venda] INTEGER NOT NULL ON CONFLICT FAIL REFERENCES tb_status_venda([id_status_venda]), [sincronizado] BOOLEAN NOT NULL DEFAULT 0);